// SPDX-License-Identifier: MIT
//pragma solidity >=0.4.22 <0.9.0;
pragma solidity ^0.5.0;
contract RideContract {
    uint public rideCount;
    mapping(uint => address) public rideContracts;

    // Khai báo sự kiện để thông báo khi một chuyến xe mới được tạo
    event RideCreated(address indexed rideAddress, uint indexed rideId);

    function createRide(string calldata _startPoint, string calldata _endPoint, uint256 _fare, uint256 _startTime) external {
        require(_fare > 0, "Fare must be greater than zero");
        rideCount++; // Tăng số lượng chuyến xe

        // Tạo một hợp đồng mới cho chuyến xe
        Ride ride = new Ride(msg.sender, _startPoint, _endPoint, _fare, _startTime);
        rideContracts[rideCount] = address(ride);

        // Phát ra sự kiện RideCreated để thông báo rằng một chuyến xe mới đã được tạo
        emit RideCreated(address(ride), rideCount);
    }

}

contract Ride {
    address payable public driver;
    string public startPoint;
    string public endPoint;
    uint256 public fare;
    uint256 public startTime;
    uint public numOfPassengers;
    mapping(address => uint256) public passengerBalances;

    constructor(address payable _driver, string memory _startPoint, string memory _endPoint, uint256 _fare, uint256 _startTime) public {
        driver = _driver;
        startPoint = _startPoint;
        endPoint = _endPoint;
        fare = _fare;
        startTime = _startTime;
        numOfPassengers = 0;
    }

    function joinRide() external payable {
        require(msg.value == fare, "Incorrect fare amount");
        passengerBalances[msg.sender] += msg.value;
        numOfPassengers++;
    }
}
