// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract RideContract {
    uint public rideCount = 0;
    struct Ride {
        uint id; 
        address driver;
        string startPoint;
        string endPoint;
        uint256 fare;
        uint256 startTime;
    }


    mapping(uint => Ride) public rides;
    constructor() public {
            createRide("Start Point", "End Point", 100); // Tạo một chuyến xe mẫu khi khởi tạo
        }

    function createRide(string memory _startPoint, string memory _endPoint, uint256 _fare) public {
        rideCount++; // Tăng số lượng chuyến xe

        // Tạo một chuyến xe mới
        Ride memory newRide = Ride(rideCount, msg.sender, _startPoint, _endPoint, _fare, block.timestamp);

        // Lưu trữ chuyến xe vào mapping
        rides[rideCount] = newRide;
    }

}
